        bkList.append("Lukka", null); bkList.append("Yokaana", null); bkList.append("Ebikolwa", null); bkList.append("Abaruumi", null); 
        try{bkList.setSelectedIndex(0, true);}catch(Exception e){e.printStackTrace();}
        biblePage.append(bkList);
